import React, { useCallback, useEffect, useState } from 'react';
import { Button, Form, Input } from 'antd';
import Link from 'next/link';
import { useDispatch, useSelector } from 'react-redux';
// import Router from 'next/router';
// import Router from 'next/router';
import Router from 'next/router';
import { loginRequestAction, SIGN_UP_SUCCESS } from '../reducers/user';

const login = () => {
  const { logInDone, logInLoading, logInError, me } = useSelector((state) => state.user);
  const dispatch = useDispatch();
  const [id, setId] = useState('');
  const [password, setPassword] = useState('');

  const onChangeId = useCallback((e) => {
    setId(e.target.value);
  }, []);

  const onChangePassword = useCallback((e) => {
    setPassword(e.target.value);
  }, []);

  useEffect(() => {
    if (logInError) {
      alert(logInError);
    }
  }, [logInError]);

  useEffect(() => {
    if (logInDone) {
      Router.push('/');
      dispatch({
        type: SIGN_UP_SUCCESS,
        // 로그인 상태일때, 회원가입창 못가게 만들어줌.
      });
    }
  }, [logInDone]);

  const onSubmitForm = useCallback(() => {
    console.log(id, password);
    dispatch(loginRequestAction({ data: id, password }));
  }, [id, password]);

  return (
    <div style={{ width: '1000px', margin: '0 auto' }}>
      <div style={{ margin: 20 }} />
      <Form onFinish={onSubmitForm} style={{ margin: 20 }}>
        <div>
          <label htmlFor="user-id">아이디</label>
          <br />
          <input name="user-id" value={id} onChange={onChangeId} required />
        </div>
        <div style={{ marginTop: '5px' }}>
          <label htmlFor="user-password">비밀번호</label>
          <br />
          <Input
            name="user-password"
            type="password"
            value={password}
            onChange={onChangePassword}
            required
          />
        </div>
        <div>
          <Button style={{ marginTop: 15 }} htmlType="submit" loading={logInLoading}>로그인</Button>
          &nbsp;&nbsp;
          <Link href="/Signup"><a><Button>회원가입</Button></a></Link>
        </div>
      </Form>
    </div>
  );
};

export default login;
