import React, { useCallback, useState } from 'react';
import { Form, Input, Link, Button } from 'antd';
import { useSelector, useDispatch } from 'react-redux';

const PostForm = () => {
  const loadPostLoading = useSelector((state) => state.post.loadPostLoading);
  const dispatch = useDispatch();
  const [title, setTitle] = useState('');
  const [description, setDescription] = useState('');

  const onChangeTitle = useCallback((e) => {
    setTitle(e.target.value);
  }, []);

  const onChangeDescription = useCallback((e) => {
    setDescription(e.target.value);
  }, []);

  const onSubmitForm = useCallback(() => {
    dispatch(postRequestAction({ title, description }));
    console.log(title, description);
  }, [title, description]);
  return (
    <div style={{ width: '1000px', margin: '0 auto' }}>
      <div style={{ margin: 20 }} />
      <Form onFinish={onSubmitForm} style={{ margin: 20 }}>
        <div>
          <label htmlFor="post-title">제목</label>
          <br />
          <input name="post-title" style={{ width: '100%' }} value={title} onChange={onChangeTitle} required />
        </div>
        <div style={{ marginTop: 10 }}>
          <label htmlFor="post-description">내용</label>
          <br />
          <input name="post-description" value={description} onChange={onChangeDescription} required />
        </div>
        <div>
          <Button style={{ marginTop: 15 }} htmlType="submit" loading={loadPostLoading}>글쓰기</Button>
        </div>
      </Form>
    </div>
  );
};

export default PostForm;
