import React, { useCallback, useState } from 'react';
import { Button, Form, Input } from 'antd';
import Link from 'next/link';
import { useDispatch, useSelector } from 'react-redux';
// import Router from 'next/router';
import Router from 'next/router';
import { loginRequestAction, LOG_IN_SUCCESS } from '../reducers/user';

const login = () => {
  const logInLoading = useSelector((state) => state.user.logInLoading);
  const logInDone = useSelector((state) => state.user.logInDone);
  const dispatch = useDispatch();
  const [id, setId] = useState('');
  const [password, setPassword] = useState('');

  const onChangeId = useCallback((e) => {
    setId(e.target.value);
  }, []);

  const onChangePassword = useCallback((e) => {
    setPassword(e.target.value);
  }, []);

  const onSubmitForm = useCallback(() => {
    dispatch(loginRequestAction({ id, password }));
    if (LOG_IN_SUCCESS === true) {
      Router.push('/');
    }
  }, [id, password, logInDone]);
  // const onSubmitForm = useCallback(() => {
  //   dispatch(loginAction({ id, password }));
  //   if (isLoggedIn === true) (
  //       <Link href="/" />
  //     )
  //   });
  // }, [id, password]);
  // if (logInDone === true) {
  //   Router.push('/');
  //   setTimeout(1000);
  //   console.log(logInDone);
  // }

  return (
    <div style={{ width: '1000px', margin: '0 auto' }}>
      <div style={{ margin: 20 }} />
      <Form action="/Login" method="POST" onFinish={onSubmitForm} style={{ margin: 20 }}>
        <div>
          <label htmlFor="user-id">아이디</label>
          <br />
          <input name="user-id" value={id} onChange={onChangeId} required />
        </div>
        <div style={{ marginTop: '5px' }}>
          <label htmlFor="user-password">비밀번호</label>
          <br />
          <Input
            name="user-password"
            type="password"
            value={password}
            onChange={onChangePassword}
            required
          />
        </div>
        <div>
          <Button style={{ marginTop: 15 }} htmlType="submit" loading={logInLoading}>로그인</Button>
          &nbsp;&nbsp;
          <Link href="/Signup"><a><Button>회원가입</Button></a></Link>
        </div>
      </Form>
    </div>
  );
};

export default login;
